$(document).ready(function(){$("tbody").scroll(function(){$("thead").css("left",-$("tbody").scrollLeft());$("thead th:nth-child(1)").css("left",$("tbody").scrollLeft());$("tbody td:nth-child(1)").css("left",$("tbody").scrollLeft())})});const App=new Vue({el:"#app",data:function(){return{realtimeData:[],now:new Date,model:{isMetricUnits:!1,isWindChill:null}}},created:function(){const n=this;this.model.isMetricUnits=localStorage.getItem("isMetricUnits")==="true";localStorage.getItem("isWindChill")&&(this.model.isWindChill=localStorage.getItem("isWindChill")==="true");n.getData();setInterval(function(){n.getData()},3e5)},computed:{nowFormated:function(){return moment(this.now).format("MM/DD/YYYY HH:mm:ss")}},watch:{"model.isMetricUnits":function(n){localStorage.setItem("isMetricUnits",n.toString());this.getData()},"model.isWindChill":function(n){localStorage.setItem("isWindChill",n.toString());this.getData()}},methods:{getData:function(){const n=this;axios.get("/data/RealtimeData",{params:{isMetricUnits:n.model.isMetricUnits,isWindChill:n.model.isWindChill}}).then(function(t){n.realtimeData=t.data;Vue.nextTick(function(){n.now=new Date;$('[data-toggle="tooltip"]').tooltip({boundary:"span",placement:"left"})})}).catch(function(n){console.log("getRealtimeData failed",n)})}}});