$(function(){$("#datePicker").datetimepicker(Core.dateTimePickerDateOptions);$("#timePicker").datetimepicker(Core.dateTimePickerTimeOptions);$('[data-toggle="tooltip"]').tooltip()});const App=new Vue({el:"#app",data:function(){return{statInfo:{},metData:{},now:new Date,model:{stationId:23,dateTime:now}}},created:function(){const n=this;Core.populateStationDropdown(this,!1);n.getData();setInterval(function(){n.getData()},3e5)},computed:{currentDate:function(){return moment().format("MM/DD/YYYY")},currentTime:function(){return moment().format("HH:mm:ss")}},methods:{getData:function(){const n=this;axios.get("/data/StationInfo",{params:{stationid:this.model.stationId,dateTime:this.model.dateTime}}).then(function(t){n.statInfo=t.data}).catch(function(n){console.log("getMeteorologicalData failed",n)});axios.get("/data/StationData",{params:{stationid:this.model.stationId,dateTime:this.model.dateTime}}).then(function(t){n.metData=t.data;Vue.nextTick(function(){n.now=new Date;$('[data-toggle="tooltip"]').tooltip({boundary:"span",placement:"left"})})}).catch(function(n){console.log("getMeteorologicalData failed",n)})}},watch:{"model.stationId":function(){self.getData()},"model.dateTime":function(){self.getData()}}});